import React from 'react';
import Sidebar from '../Components/Sidebar'
import {FormInput , FormSelect , MultiFormSelect} from '../Components/Form'
import moment from 'moment';
import { omit } from 'lodash'

import PRO_CONTROLLER from '../Controllers/Product.Controller';
import CONFIG from '../Controllers/Config.Controller';

// import { ThemeProvider, createGlobalStyle } from 'styled-components';




class AddProduct extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            packageName: '',
            packageProduct: '',
            price: '',
            duration: '',
            quntity: '',
            errors : { 
                packageName : false , 
                packageProduct : false ,
                price : false ,
                duration : false ,
                quntity : false ,
            }
        };
    }

    formValueChange = (e) => {
        this.setState({[e.target.name] : e.target.value  });
    }

    clear = ()=>{
        this.setState({
            packageName: '',
            packageProduct: '',
            price: '',
            duration: '',
            quntity: '',
        })
    }

    onFormSubmit = async (e) => {
        e.preventDefault();
        var ret = this.validate();
        console.log(ret);
        if (ret == false){
            console.log("Before");
            return "";
        }

        console.log("After");
        var data = {
            packageName: this.state.packageName,
            packageProduct: this.state.packageProduct,
            price: this.state.price,
            duration: this.state.duration,
            quntity: this.state.quntity,
        }

        const result = await PRO_CONTROLLER.AddProduct(data)

        console.log(result);

        if(result == 200){
            CONFIG.showAlert("Successfully Added")
            this.clear();
        }
    }

    render(){
        const {errors} = this.state;
    return (

        <div className="app" >
        <Sidebar activemenu={'PACKAGE'} submenu={'ADD_PACKAGE'} />
        <main>
            <div className="container-fluid" >
            <div className="row" >
                <div className="col-12 shadow-sm rounded bg-white mt-1" >
                    <h6 className="text-header py-3 mb-0 font-weight-bold line-hight-1">Add Product<br></br>
                    <span className="text-muted small">Add a new Package to the system</span></h6>
                </div>
                <div className="col-12 shadow-sm rounded bg-white mt-3 pb-1" >
                <form onSubmit={(e) => this.onFormSubmit(e)}>
                <div className="row mt-1 pb-3" >
                    <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Name'}
                                placeholder={'Enter package name'}
                                error={ errors.packageName}
                                name="packageName"
                                value={this.state.packageName}
                                onChange={this.formValueChange}
                                error_meesage={'*Package name is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Package Product'}
                                placeholder={'Enter package product'}
                                error={ errors.packageProduct}
                                name="packageProduct"
                                value={this.state.packageProduct}
                                onChange={this.formValueChange}
                                error_meesage={'*Package produc is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Price'}
                                placeholder={'Enter price'}
                                error={ errors.price}
                                name="price"
                                value={this.state.price}
                                onChange={this.formValueChange}
                                error_meesage={'*Price is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                        <FormSelect 
                                label={'Duration'}
                                options={AT_OPTIONS}
                                error={ errors.duration}
                                selected={this.state.duration}
                                onChange={this.formValueChange}
                                name="duration"
                                error_meesage={'*Duration is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Quntity'}
                                placeholder={'Enter quntity'}
                                error={ errors.quntity}
                                name="quntity"
                                value={this.state.quntity}
                                onChange={this.formValueChange}
                                error_meesage={'*Number of quntity is required'}
                            />
                    </div>
                    <div className="col-md-12 mt-1 mb-1" >
                            <button type="submit" className="btn btn-primary">Submit</button>
                            {/* <button type="reset" className="btn-outline-secondary mt-2 btn btn-sm px-2 " onClick={this.clear}>Reset</button> */}
                    </div>
                </div>
                </form>
                </div>
            </div>
            </div>
        </main>
        
    </div>

    );}

    validate = () => {
        let { packageName, packageProduct, price, duration, quntity} = this.state;
        let count = 0;
        let errors = {}

        if( packageName == ''){
            errors.packageName = true
            count++
        }else{
            errors.packageName = false 
        }

        if( packageProduct == ''){
            errors.packageProduct = true
            count++
        }else{
            errors.packageProduct = false 
        }

        if( price == ''){
            errors.price = true
            count++
        }else{
            errors.price = false 
        }

        if( duration == ''){
            errors.duration = true
            count++
        }else{
            errors.duration = false 
        }

        if( quntity == ''){
            errors.quntity = true
            count++
        }else{
            errors.quntity = false 
        }
       
        this.setState({errors})
        return count == 0;
    }

    ResetForm = () => {
        this.setState(
            {
                packageName: '',
                packageProduct: '',
                price: '',
                duration: '',
                quntity: ''
            }
        )
    }
}

const AT_OPTIONS = [{ label : 'Select Access Time' ,value : "" } , 
...['1 month', '2 months', '3 months', '4 months'].map( i => {
    return{
        label :  i  ,
         value : i 
    }
})];

export default AddProduct;
