import React from 'react';
import Sidebar from '../Components/Sidebar'
import { Link } from 'react-router-dom';
import { jsPDF } from "jspdf";
import 'jspdf-autotable';
import P_CONTROLLER from '../Controllers/Product.Controller';
import { faBorderStyle } from '@fortawesome/free-solid-svg-icons';

class ProductList extends React.Component {

  constructor(props){
      super(props);
      this.state = {
          productList: [],
      }
  }
  
  async componentDidMount() {
      const res = await P_CONTROLLER.getAllProduct();
      this.setState({
        productList: res
      });
  }
  pdfGenarator = () => {
    
    var doc = new jsPDF();

    // doc.text(20,20, 'This is defult')

    // var cols = ["Header 1", "Header 2", "Header 3"];
    // var data = [
    //             ["Row 1", "Row 1", "Row 1"],
    //             ["Row 1", "Row 1", "Row 1"],
    //             ["Row 1", "Row 1", "Row 1"]
    //         ];

    // var doc = new jsPDF('p', 'pt');
    // doc.autoTable(cols, {renderTable});
    // doc.save("table.pdf");

    doc.autoTable({html: "#my-table"})
    doc.autoTable({
        head: [['name','Email']],
        body: [
            
        ]
      
    })
    doc.save("genarated.pdf")
    
  }


  render(){
      const {productList} = this.state;
  return (
    <div className="app" >
    <Sidebar activemenu={'PACKAGE'} submenu={'PACKAGE_LIST'} />
    <main>
        <div className="container-fluid" >
        <div className="row" >
            <div className="col-12 shadow-sm rounded bg-white mt-1" >
                <h6 className="text-header py-3 mb-0 font-weight-bold line-hight-1">Packages<br></br>
                <span className="text-muted small">Dashboard</span></h6>
            </div>

            <div className="col-12 shadow-sm rounded bg-white mt-3" >
                <table class="table borderless customtable" id="my-table">
                    <thead>
                        <tr>
                        <div >
                        <th className="font-08 text-dark2 ">Package ID</th>
                        <th className="font-08 text-dark2 ">Package Name</th>
                        <th className="font-08 text-dark2 ">Package Product</th>
                        <th className="font-08 text-dark2 ">Price</th>
                        <th className="font-08 text-dark2 ">Duration</th>
                        <th className="font-08 text-dark2 ">Quntity</th>
                        </div>
                        <th className="font-08 text-dark2 ">Actions</th>
                        </tr>
                    </thead>
                    <tbody >
                        {productList && productList.map((value , i) => this.renderTable(value , i))}
                      
                    </tbody>
                </table>
            </div>
            <div className="col-12 shadow-sm rounded bg-white mt-1" >
                <h6 className="text-header py-3 mb-0 font-weight-bold line-hight-1">Get package list as a pdf</h6>
                <button type="submit" onClick={this.pdfGenarator} className="btn-outline-primary mt-2 btn btn-sm px-2 mr-4">Get PDF</button><h6></h6>
            </div>
        </div>
        </div>
    </main>
  </div>
  );}

  renderTable = (product , index) => {
      console.log(product);
    return (
                        <tr key={product._id}>
                            <td>{`P${("00" + (index + 1)).slice(-3)}`}</td>
                            <td>{product.packageName}</td>
                            <td>{product.packageProduct}</td>
                            <td>{product.price}</td>
                            <td>{product.duration}</td>
                            <td>{product.quntity}</td>
                            <td><Link to={"/package/edit/" + product._id}><span className="badge badge-info rounded-0 bg-white text-success border border-secondary click font-weight-bold ">Edit</span></Link>
                            <Link to={"/package/delete/" + product._id}><span className="badge badge-info rounded-0 bg-white text-danger border border-secondary click font-weight-bold ml-2">Delete</span></Link>
                            </td>
                        </tr>
    )
  }
}

export default ProductList;
