import React from 'react';
import Sidebar from '../Components/Sidebar'
import {FormInput , FormSelect , MultiFormSelect} from '../Components/Form'

import { Link } from 'react-router-dom';

import PACK_CONTROLLER from '../Controllers/Product.Controller';
import CONFIG from '../Controllers/Config.Controller';

class EditPackage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            packageName: '',
            packageProduct: '',
            price: '',
            duration: '',
            quntity: '',
            id: '',
            errors : { 
                packageName : false , 
                packageProduct : false ,
                price : false ,
                duration : false ,
                quntity : false ,
            }
        };
    }

    async componentDidMount() {
        console.log("Package ID: ", this.props.match.params.id);
        
        const result = await PACK_CONTROLLER.getOne(this.props.match.params.id);

        console.log("Package results: ", result.data);

        this.setState({
            packageName: result.data.packageName,
            packageProduct: result.data.packageProduct,
            price: result.data.price,
            duration: result.data.duration,
            quntity: result.data.quntity,
            id: result.data._id
        })
    }

    formValueChange = (e) => {
        this.setState({[e.target.name] : e.target.value  });
    }

    onFormSubmit = async (e) => {
        e.preventDefault();
        this.validate();

        var data = {
            id: this.state.id,
            packageName: this.state.packageName,
            packageProduct: this.state.packageProduct,
            price: this.state.price,
            duration: this.state.duration,
            quntity: this.state.quntity,
        }

        const result = await PACK_CONTROLLER.editProduct(data)

        console.log(result);

        if(result == 200){
            CONFIG.showAlert("Successfully Updated");
            document.getElementById("cancelBtn").click();
            //this.clear();
        }
    }

    render(){
        const {errors} = this.state;
    return (
        <div className="app" >
        <Sidebar activemenu={'PACKAGE'} submenu={'PACKAGE_LIST'} />
        <main>
            <div className="container-fluid" >
            <div className="row" >
                <div className="col-12 shadow-sm rounded bg-white mt-1" >
                    <h6 className="text-header py-3 mb-0 font-weight-bold line-hight-1">Edit Package<br></br>
                    <span className="text-muted small">Editing package details</span></h6>
                </div>
                <div className="col-12 shadow-sm rounded bg-white mt-3 pb-1" >
                <form onSubmit={(e) => this.onFormSubmit(e)}>
                <div className="row mt-1 pb-3" >
                <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Name'}
                                placeholder={'Enter package name'}
                                error={ errors.packageName}
                                name="packageName"
                                value={this.state.packageName}
                                onChange={this.formValueChange}
                                error_meesage={'*Package name is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Package Product'}
                                placeholder={'Enter package product'}
                                error={ errors.packageProduct}
                                name="packageProduct"
                                value={this.state.packageProduct}
                                onChange={this.formValueChange}
                                error_meesage={'*Package produc is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Price'}
                                placeholder={'Enter price'}
                                error={ errors.price}
                                name="price"
                                value={this.state.price}
                                onChange={this.formValueChange}
                                error_meesage={'*Price is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                        <FormSelect 
                                label={'Duration'}
                                options={AT_OPTIONS}
                                error={ errors.duration}
                                selected={this.state.duration}
                                onChange={this.formValueChange}
                                name="duration"
                                error_meesage={'*Duration is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Quntity'}
                                placeholder={'Enter quntity'}
                                error={ errors.quntity}
                                name="quntity"
                                value={this.state.quntity}
                                onChange={this.formValueChange}
                                error_meesage={'*Number of quntity is required'}
                            />
                    </div>
                    <div className="col-md-12 mt-1 mb-1" >
                            <button type="submit" className="btn-outline-primary mt-2 btn btn-sm px-2 mr-4">Update</button>
                            <Link to="/package/list"><button id="cancelBtn" className="btn-outline-secondary mt-2 btn btn-sm px-2 ">Cancel</button></Link>
                    </div>
                </div>
                </form>
                </div>
            </div>
            </div>
        </main>
    </div>
    );}

    validate = () => {
        let { packageName, packageProduct, price, duration, quntity} = this.state;
        let count = 0;
        let errors = {}

        if( packageName == ''){
            errors.packageName = true
            count++
        }else{
            errors.packageName = false 
        }

        if( packageProduct == ''){
            errors.packageProduct = true
            count++
        }else{
            errors.packageProduct = false 
        }

        if( price == ''){
            errors.price = true
            count++
        }else{
            errors.price = false 
        }

        if( duration == ''){
            errors.duration = true
            count++
        }else{
            errors.duration = false 
        }

        if( quntity == ''){
            errors.quntity = true
            count++
        }else{
            errors.quntity = false 
        }
       
        this.setState({errors})
        return count == 0;
    }


    clear = ()=>{
        this.setState(
            {
                packageName: '',
                packageProduct: '',
                price: '',
                duration: '',
                quntity: ''
            }
        )
    }
}

const AT_OPTIONS = [{ label : 'Select Access Time' ,value : "" } , 
...['1 month', '2 months', '3 months', '4 months'].map( i => {
    return{
        label :  i  ,
         value : i 
    }
})];

export default EditPackage;
