import React from 'react';


import { withRouter } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faBackspace , faExclamationTriangle } from '@fortawesome/free-solid-svg-icons';
import Axios from "axios";
import Config from "./Controllers/Config.Controller";

class SplashScreen extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            loading : true,
            failed : false,
        };
    }

    componentDidMount(){
        this.checkConnection();
        
    }

    checkConnection = () => {
        this.setState({loading : true , failed : false})
        setTimeout( () => {
            Axios.get(`${Config.host}${Config.port}/api/generate/get/test`)
                .then(Response => {
                    if(Response.status == 200){
                        this.props.history.push('/dashboard');
                    }
                })
                .catch(err => {
                    this.setState({loading : false , failed : true})
                    
                });
            }, 1600);
    }


    render(){
       
    return (
        <div className="app" >
            
            <div id="loading-wrapper">
            { this.state.loading && 
            <>
            <div id="loading-text">Loading..</div>
            <div id="loading-content"></div> </>}

           {
               !this.state.loading &&  this.state.failed &&
                <div className="shadow-sm rounded bg-white mt-5 splash-error p-3" >
                    <h6 className="text-header text-warning pt-2 pb-2 mb-0 font-weight-bold ">
                        <FontAwesomeIcon icon={faExclamationTriangle}  className="mr-2"/>Network Connection Failed !
                    </h6>
                    <h6 className="text-header mb-0  line-hight-1">
                    <span className="text-muted small font-weight-bold">Check your internet connection and try again.<br></br>
                    We Couldn't Connected to the server</span></h6>

                    <button onClick={this.checkConnection} className="btn btn-sm btn-dark mt-2">Try Again</button>
                </div>
               }
           
            </div>

            
        </div>
    );
    }

   



    
}

export default withRouter(SplashScreen);
