import React from 'react';
import Sidebar from '../Components/Sidebar'
import { Link, Router } from 'react-router-dom';

import P_CONTROLLER from '../Controllers/Product.Controller';
import CONFIG from '../Controllers/Config.Controller';

class DeletePackage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            packageName: '',
            packageProduct: '',
            price: '',
            duration: '',
            quntity: '',
            id: '',
        };
    }

    async componentDidMount() {
        console.log("Product ID: ", this.props.match.params.id);
        
        const result = await P_CONTROLLER.getOne(this.props.match.params.id);

        console.log("Product results: ", result.data);

        this.setState({
            packageName: result.data.packageName,
            packageProduct: result.data.packageProduct,
            price: result.data.price,
            duration: result.data.duration,
            quntity: result.data.quntity,
            id: result.data._id,
        })
    }

    render(){

    return (
        <div className="app" >
        <Sidebar activemenu={'PACKAGE'} submenu={'PACKAGE_LIST'} />
        <main>
            <div className="container-fluid" >
            <div className="row" >
                <div className="col-12 shadow-sm rounded bg-white mt-1" >
                    <h6 className="text-header py-3 mb-0 font-weight-bold line-hight-1">Delete Package<br></br>
                    <span className="text-muted small">Package delete confirmation</span></h6>
                </div>
                <div className="col-12 shadow-sm rounded bg-white mt-3 pb-1" >
    
                <div className="row mt-1 pb-3" >
                    <div className="col-md-12 mt-2" >
                    <h6 style={{fontSize : '0.9rem'}}
                        className="text-header text-danger mb-0 font-weight-bold line-hight-1">Are you sure you want to delete this Package ?<br></br>
                    <span className="text-muted small">This process can't be undone, All the relevant details will be permanently deleted !</span></h6>
                    </div>
                    <div className="col-md-12 mt-3" >
                        <table class="table borderless customtable mb-0">
                            <thead>
                            <tr>
                            <th className="font-08 text-dark2 ">Package Name</th>
                            <th className="font-08 text-dark2 ">Package Product</th>
                            <th className="font-08 text-dark2 ">Price</th>
                            <th className="font-08 text-dark2 ">Duration</th>
                            <th className="font-08 text-dark2 ">Quntity</th>
                            </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>{this.state.packageName}</td>
                                    <td>{this.state.packageProduct}</td>
                                    <td>{this.state.price}</td>
                                    <td>{this.state.duration}</td>
                                    <td>{this.state.quntity}</td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                    <div className="col-md-12 mt-1 mb-1" >
                    <hr className="my-2"></hr>
                            <button className="btn-danger mt-2 btn btn-sm px-3 py-1" onClick={this.onDelete}>Delete</button>
                            <Link to="/package/list"><button type="submit" id="cancelBtn" className="btn-light mt-2 btn btn-sm px-3 py-1 border mx-2">Cancel</button></Link>
                    </div>
                </div>
                </div>
            </div>
            </div>
        </main>
    </div>
    );}

    onDelete = async () => {
        const data = {
            id: this.state.id,
            name: this.state.name,
        }
        const result = await P_CONTROLLER.deleteProduct(data);
        if(result == 200){
            CONFIG.showAlert("Successfully Deleted");
            document.getElementById("cancelBtn").click();
        }
        console.log(result);
    }

}

export default DeletePackage;
