import React from 'react';
import Sidebar from '../Components/Sidebar'
import { Link } from 'react-router-dom';
import { jsPDF } from "jspdf";
import 'jspdf-autotable';
import MAC_CONTROLLER from '../Controllers/Machine.Controller';
import { faBorderStyle } from '@fortawesome/free-solid-svg-icons';


class MachineList extends React.Component {

  constructor(props){
      super(props);
      this.state = {
          machineList: [],
      }
  }
  
  async componentDidMount() {
      const res = await MAC_CONTROLLER.getAllMachine();
      this.setState({
        machineList: res
      });
  }
  pdfGenarator = () => {
    
    var doc = new jsPDF();
    doc.text(10, 10, 'VESLOO HOLDINGS');
  
    doc.autoTable({html: "#machine-table"})
    doc.save("machine.pdf")
    
  }


  render(){
      const {machineList} = this.state;
  return (
    <div className="app" >
    <Sidebar activemenu={'PACKAGE'} submenu={'PACKAGE_LIST'} />
    <main>
        <div className="container-fluid" >
        <div className="row" >
            <div className="col-12 shadow-sm rounded bg-white mt-1" >
                <h6 className="text-header py-3 mb-0 font-weight-bold line-hight-1">Machine List</h6>
                <div className="col-12 shadow-sm rounded bg-white mt-3" >
                <table class="table borderless customtable" id="machine-table">
                    <thead>
                        <tr>
                        <th className="font-08 text-dark2 ">Machine Name</th>
                        <th className="font-08 text-dark2 ">Machine Model Number</th>
                        <th className="font-08 text-dark2 ">Department</th>
                        <th className="font-08 text-dark2 ">Machine Serial Number</th>
                        <th className="font-08 text-dark2 ">Date</th>
                        <div>
                        <th className="font-08 text-dark2 ">Actions</th>
                        </div>
                        </tr>
                    </thead>
                    <tbody >
                        {machineList && machineList.map((value , i) => this.renderTable(value , i))}
                      
                    </tbody>
                </table>
            </div>
            </div>


            <div className="col-12 shadow-sm rounded bg-white mt-1" >
                <h6 className="text-header py-3 mb-0 font-weight-bold line-hight-1">Get package list as a pdf</h6>
                <button type="submit" onClick={this.pdfGenarator} className="btn btn-danger">Get PDF</button><h6></h6>
            </div>
        </div>
        </div>
    </main>
  </div>
  );}

  renderTable = (machine , index) => {
      console.log(machine);
    return (
                        <tr key={machine._id}>
                            <td>{machine.machineName}</td>
                            <td>{machine.machineModelNumber}</td>
                            <td>{machine.machineDepartment}</td>
                            <td>{machine.machineSerialNumber}</td>
                            <td>{machine.machineDate}</td>
                            <td><Link to={"/Machine/edit/" + machine._id}><span className="btn btn-danger margin-left">Edit</span></Link>
                            <Link to={"/Machine/delete/" + machine._id}><span className="btn btn-warning">Delete</span></Link>
                            </td>
                        </tr>
    )
  }
}

export default MachineList;
